package com.class2;

public class TasksAritmaticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// Task 1 
		
		double a= 32.5;
		double b= 11.3;
		
		
		double sum= a+b;
		double subs= a-b;
		double mult= a*b;
		double div= a/b;
		
		System.out.println(sum);
		System.out.println(subs);
		System.out.println(mult);
		System.out.println(div);
		
		System.out.println("the sum of a and b is " + sum);
		System.out.println("the substraction of a and b is " + subs);
		System.out.println("the multiple of a and b is " + mult);
		System.out.println("the division of a and b is " + div);
		
		// Task 2
		
		
		float x= 3.9f;
		
		double square = x*x;
		
		System.out.println("the square of 3.9 is " + square);
		
		// Task 3
		
		
		int w=5;
		int h=8;
		
		int perimeter= 2*(w+h);
		int area= w*h;
		
		System.out.println("the perimeter of rectangular is " + perimeter);
		
		System.out.println("the area of rectangular is " + area);
		
		int r=5;
		double Pi=3.14;
		
		double volume= 4/3 * (Pi) * (r*r*r);
		
		
		System.out.println(
				"The Volume of the Sphere with raidus 5cm is " 
		+volume+ " cm^3");
		
	}
}


	
		

		
			
			
			
