package com.class3;

public class ifElseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int day = 6;
		
		if (day==1) {System.out.println("Monday");}
		
		else if (day ==2) {System.out.println("Thuesday");}
		
		else if (day==3) {System.out.println("Wednesday");}
		
		else if (day==4) {System.out.println("thursday");}
		
		else if (day ==5) {System.out.println("friday a Fun day");}
		
		else if (day ==6) {System.out.println("Saturday is a Coding day");}
		
		else {System.out.println("Sunday is a Coding day");}
		
		
	}

}
