package com.class8;

public class nestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int i=0; i<3; i++) {
			
			
			for (int a=0; a<3; a++) {
				
				System.out.println("\ninner loop");
				
			}
			
			System.out.println("\n\touter loop");
			
		}
		
		
	}

}
